mapper.select:
select

from warehouse
where iddeleted = 0

and name = #{name}

order by warehouseno asc
limit #{start}, #{limit}

mapper.update:
update warehouse
set NAME = #{name,jdbcType=VARCHAR},
  REMARK = #{remark,jdbcType=VARCHAR},
  IDDELETED = #{idDeleted,jdbcType=INTEGER},
  WarehouseNo = #{warehouseno,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}
